<?php	function selLang($la='ru'){		$sql = "SELECT * FROM lang WHERE lang='$la'";		$curlang = mysql_query($sql) or die(mysql_error());		$lang = mysql_fetch_assoc($curlang);		$_SESSION['lang'] = $la;		return $lang;	}	function clearData($data){		return  htmlspecialchars(addslashes(trim($data)));	}	function fetchToArr($sel){		while($ar = mysql_fetch_array($sel)){			$arr[] = $ar;		}		if (!empty($arr))			return $arr;	}	function aunt($login,$pass){		$sql = "SELECT pass, status FROM users WHERE login='$login'";		$user = mysql_query($sql) or die(mysql_error());		$dbpass = mysql_fetch_array($user);		if(empty($dbpass)){			global $lang;			$_SESSION['error'] = $lang['invalidpass'];			header("Location: index.php");			exit;		}		if($dbpass['pass'] == md5($pass)){			$_SESSION['name'] = $login;			$_SESSION['status'] = $dbpass['status'];			unset($_SESSION['error']);			header("Location: index.php");		}else{			global $lang;			$_SESSION['error'] = $lang['invalidpass'];			header("Location: index.php");			exit;		}	}	function reg($login, $pass){		$sql = "SELECT login FROM users WHERE login='$login'";		$user = mysql_query($sql) or die(mysql_error());		$dbpass = mysql_fetch_array($user);		if(!empty($dbpass)){			global $lang;			$_SESSION['error'] = $lang['dublicate'];			header("Location: index.php");			exit;		}else{			$pass = md5($pass);			$sql = "INSERT INTO users (login, pass) VALUES ('$login','$pass')";			$act = mysql_query($sql) or die(mysql_error());			if($act){				$_SESSION['name'] = $login;			}		}		header("Location: index.php");	}	function countUser(){		$sql = "SELECT COUNT(*) FROM users";		$res = mysql_query($sql) or die(mysql_error());		$row = mysql_fetch_row($res);		return $row[0];	}	function getMenu($status = 0){		$sql = "SELECT COUNT(*) FROM users";		$res = mysql_query($sql) or die(mysql_error());		$row = mysql_fetch_row($res);		if($row){			$sql = "SELECT * FROM menu";			$menu = mysql_query($sql) or die(mysql_error());			$m = fetchToArr($menu);			return $m;		}			}	function getText($id){		$sql = "SELECT text FROM menu WHERE id=$id";		$text = mysql_query($sql) or die(mysql_error());;		return mysql_result($text,0);	}	function delList(){		$sql = "SELECT id, title FROM menu";		$delList = mysql_query($sql) or die(mysql_error());		return fetchToArr($delList);	}	function vievList($id){		$sql = "SELECT title FROM menu WHERE id='$id'";		$delList = mysql_query($sql) or die(mysql_error());		return mysql_result($delList,0);	}	function add($title,$text){		$sql = "INSERT INTO menu (title, text) VALUES ('$title', '$text')";		$addstatus = mysql_query($sql) or die(mysql_error());		if ($addstatus) 			$_SESSION['addstatus'] = "Страница добавлена";		else			$_SESSION['addstatus'] = "Страница не добавлена";		header("Location: index.php?add");	}	function update($id,$title,$text){		$sql = "UPDATE menu set title='$title', text='$text' WHERE id='$id'";		mysql_query($sql) or die(mysql_error());		header("Location: index.php");	}		function delMenu($id){		$sql = "DELETE FROM menu WHERE id=$id";		mysql_query($sql);	}	function getUser(){		$sql = "SELECT login, status FROM users";		$users = mysql_query($sql) or die(mysql_error());		$getusers = fetchToArr($users);		return $getusers;	}	function role($user,$role){		$sql = "SELECT status FROM users WHERE login='$user'";		$sqlstatus = mysql_query($sql) or die(mysql_error());		$status = mysql_result($sqlstatus,0);		if ($role != $status){			$sql = "UPDATE users SET status='$role' WHERE login='$user'";			mysql_query($sql);			header("Location: index.php?role");		}		header("Location: index.php?role");	}?>	